mapper.select:
	
		SELECT *
		FROM 
		(SELECT ROWNUM rn, a.* 
      		FROM 
      		(SELECT * FROM emp ORDER BY empno) a
            )  
		WHERE rn >= #{start} 
		AND rn <= #{end}

mapper.update:
UPDATE emp SET 
	ename = #{ename}, job = #{job}, sal = #{sal}, hiredate = #{hiredate},
	 comm = #{comm}, 
	mgr = #{mgr}, deptno = #{deptno}
WHERE empno = #{empno}

mapper.select:
SELECT ROWNUM, a.* FROM (SELECT * FROM emp ORDER BY empno) a WHERE ROWNUM BETWEEN #{start} AND #{end}
AND job LIKE '%' || #{keyword} || '%'
AND ename LIKE '%' || #{keyword} || '%'
